; -*- Mode: Merlin -*-;; File: defs/maketags.equs;IfSystemVer = 0  ;1 = yes, 0 = no.; do IfSystemVerTagsTable = $3400 elseTagsTable = $7100 fin;; Some meaty stuff.;RamTop = $BF00  ;top of user RAM.;; You are *NOT* to change UserFuncAddr.  Maketags will break if; you do.  Unless, of course, you have the source, in which; case, by all means, do what is best.;MaxTPSize = $600UserFuncAddr = RamTop-MaxTPSize ;1.5 space for user tag processing.UserStartTag = UserFuncAddr ;vector to begin processing a file.UserIsTag? = UserStartTag+2 ;vector to process LineBuff for tags.UserEndTag = UserIsTag?+2 ;vector to end processing a file.;; These used to be variables, now they are equates.;SourceFile = UserFuncAddr-64 ;pathname for source file.TagsFile = SourceFile-64 ;pathname for tags file.ModesFile = TagsFile-64  ;pathname for modes files.CurrFile = ModesFile-64 ;pathname for file being processed.Tag = CurrFile-64  ;space for a large tag.DefModeText = Tag-32  ;text of default mode.CurrModeText = DefModeText-32 ;text of current mode.ModeFileText = CurrModeText-64 ;transition buffer for file proc text.ForcedMode = ModeFileText-32 ;text of mode to force on all files.;VarBottom = UserFuncAddr-$400;ModesList = VarBottom-$400 ;1k file buffer for modes list.FileBuff1 = ModesList-$400 ;1k file buffer for Open.FileBuff2 = FileBuff1-$400 ;another one.FileBuff3 = FileBuff2-$400 ;and another. 3 files.FileBuff4 = FileBuff3-$400LineBuff = FileBuff4-$100 ;line input buffer.DataBuffSz = $400DataBuffer = LineBuff-DataBuffSz ;where the source file data buffer is.;TagsTop = LineBuff  ;top of space for tags buffer.Tags = TagsTable  ;lots of room for tags.;; Some other variables that are always used.;temp = $00temp1 = temp+2temp2 = temp1+2;